CREATE TABLE `op_tags` (`ID_TAG` MEDIUMINT( 8 ) UNSIGNED NOT NULL AUTO_INCREMENT ,`tag` TINYINT NOT NULL ,INDEX ( `ID_TAG` )) ENGINE = MYISAM ;

CREATE TABLE `op_tags_links` (`ID` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY ,`ID_TAG` MEDIUMINT( 8 ) UNSIGNED NOT NULL ,`ID_QUESTION` MEDIUMINT( 8 ) UNSIGNED NOT NULL) ENGINE = MYISAM ;CREATE TABLE `op_questions` (`ID_QUESTION` INT( 10 ) NOT NULL AUTO_INCREMENT PRIMARY KEY,`ID_MEMBER` MEDIUMINT( 8 ) UNSIGNED NOT NULL ,`question` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,`creationDate` DATE NOT NULL ,`difficulty` TINYTEXT NOT NULL ,`score` TINYTEXT NOT NULL ) ENGINE = MYISAM ;

CREATE TABLE `op_answers` (`ID_ANSWER` INT( 10 ) NOT NULL AUTO_INCREMENT PRIMARY KEY ,`ID_QUESTION` INT( 10 ) UNSIGNED NOT NULL ,`right1` TINYTEXT NOT NULL ,`wrong1` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,`wrong2` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL ,`wrong3` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL) ENGINE = MYISAM ;

CREATE TABLE `op_members` ( `ID_MEMBER` MEDIUMINT(8) NOT NULL AUTO_INCREMENT PRIMARY KEY, `memberName` VARCHAR(80) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `dateRegistered` DATE NOT NULL, `password` VARCHAR(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `posts` MEDIUMINT(8) UNSIGNED NOT NULL, `quality` TINYINT(4) UNSIGNED NOT NULL, `emailAddress` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `realName` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `location` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `gender` TINYINT(4) UNSIGNED NOT NULL, `birthdate` DATE NOT NULL, `websiteUrl` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `personalText` MEDIUMTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `passwordSalt` VARCHAR(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `personalIcon` TINYTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL, `score` INT(10) NOT NULL ,`active` INT(10) NOT NULL) ENGINE = myisam;

CREATE TABLE op_sessions (   uid CHAR(32) NOT NULL,   ID_MEMBER INT UNSIGNED NOT NULL,   creation_date INT UNSIGNED NOT NULL,   INDEX(uid));
